package aoc.y2015

object Day1:
  private def navigate(cursor: Int, direction: Char): Int =
    direction match
      case '(' => cursor + 1
      case ')' => cursor - 1

  def solve(input: String): Int = input.foldLeft(0)(navigate)

  def solve2(input: String): Int = input.toSeq.scanLeft(0)(navigate).indexWhere(_ < 0)

