package aoc
package y2022

object Day17:
  private val rocks: Vector[Set[Coord]] = Vector(
    Set(Coord(0, 0), Coord(1, 0), Coord(2, 0), Coord(3, 0)),
    Set(Coord(1, 0), Coord(0, -1), Coord(1, -1), Coord(2, -1), Coord(1, -2)),
    Set(Coord(2, 0), Coord(2, -1), Coord(0, -2), Coord(1, -2), Coord(2, -2)),
    Set(Coord(0, 0), Coord(0, -1), Coord(0, -2), Coord(0, -3)),
    Set(Coord(0, 0), Coord(1, 0), Coord(0, -1), Coord(1, -1))
  )

  def solve(input: String, after: Long = 2022): Long =
    val moves = input.map {
      case '>' => Direction.Right
      case '<' => Direction.Left
    }.toVector

    LazyList
      .iterate(
        (
          Set(Coord(0, 0), Coord(1, 0), Coord(2, 0), Coord(3, 0), Coord(4, 0), Coord(5, 0), Coord(6, 0)),
          0L,
          0L,
          Coord(2L, 4L)
        )
      ): (grid, rock, move, coord) =>
        val (nextCoord, curRock) =
          val nc = coord + moves((move % moves.size).toInt).direction
          val cs = rocks((rock % rocks.size).toInt).map(nc + _)
          if cs.exists(c => c.x < 0 || 6 < c.x || grid.contains(c))
          then (coord, rocks((rock % rocks.size).toInt).map(coord + _))
          else (nc, cs)
        if curRock.exists(c => grid.contains(c + Direction.Down.direction)) then
          val newGrid  = grid ++ curRock
          val tallestY = newGrid.map(_.y).max
          (
            newGrid.filter(_.y > tallestY - 100),
            rock + 1,
            move + 1,
            Coord(2, tallestY + rocks(((rock + 1) % rocks.size).toInt).map(_.y).size + 3)
          )
        else (grid, rock, move + 1, nextCoord + Direction.Down.direction)
      .dropWhile((_, rock, _, _) => rock < after)
      .head(0)
      .map(_.y)
      .max

  def solve2(input: String): Long = solve(input, 1000000000000L)

