package aoc
package y2024

object Day4:
  def solve(input: String): Int =
    val lines = input.linesIterator.toVector
    val xs = for
      (line, y) <- lines.zipWithIndex
      x         <- line.indices
      if line.charAt(x) == 'X'
    yield Coord(x, y)

    (for
      x         <- xs
      direction <- Direction.values
      if lines.get(x + direction.direction).contains('M')
        && lines.get(x + direction.direction + direction.direction).contains('A')
        && lines.get(x + direction.direction + direction.direction + direction.direction).contains('S')
    yield x).size

  def solve2(input: String): Int =
    val lines = input.linesIterator.toVector
    val as = for
      (line, y) <- lines.zipWithIndex
      x         <- line.indices
      if line.charAt(x) == 'A'
    yield Coord(x, y)

    (for
      a         <- as
      direction <- List(Direction.UpLeft, Direction.UpRight, Direction.DownLeft, Direction.DownRight)
      if lines.get(a + direction.direction).contains('M')
        && lines.get(a + direction.oposite.direction).contains('S')
        && (lines.get(a + direction.left.direction).contains('M')
          && lines.get(a + direction.right.direction).contains('S'))
    yield a).size

